mapper.select:
SELECT COUNT(*) 
FROM qa_board
WHERE user_id = #{userId}

mapper.update:
update qa_board 
set qa_read_count = qa_read_count + 1
where id = #{id}

update qa_board
set qa_recommend_count = qa_recommend_count + 1
where id = #{id}

mapper.select:
select r2.* 
from (
    select r1.*, rownum rnum 
    from (
        select * 
        from qa_board 
        where qa_secret != 'Y'
        order by qa_recommend_count desc
    ) r1
) r2

where rnum >=1 and rnum <=5

mapper.update:
update qa_board 
set qa_category_id = #{qa_category_id}, qa_title=#{qa_title}, 
qa_content=#{qa_content}, qa_secret=#{qa_secret}
where id = #{id}

mapper.select:
select To_DATE(sysdate, 'YYYY-MM-DD') - To_DATE(qa_reg_date, 'YYYY-MM-DD') 
from qa_board
where id = #{id}
